import { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";

function ProductsPage() {
  const [productsList, setProductsList] = useState([]);

  const getDet = () => {
    fetch("http://localhost:8080/details")
      .then((response) => response.json())
      .then((allDet) => setProductsList(allDet));
  };

  useEffect(() => {
    getDet();
  }, [true]);

  const location = useLocation()
  const requiredProducts = productsList.filter((item) => item.category == location.state)
  return <div className="container productsPage my-4">
    <div className="filterSection text-center">
      <button type="button" className="btn btn-outline-dark">Bed</button>
      <button type="button" className="btn btn-outline-dark">Sofa</button>
      <button type="button" className="btn btn-outline-dark">Table</button>
      <button type="button" className="btn btn-outline-dark">hair</button>
    </div>
    <div className="products my-4">
      <div className="row row-cols-2 row-cols-sm-3 row-cols-lg-5">
        {
          requiredProducts.map((item) => {
            return (
              < div className="col mb-4" key={item.id}>
                <div className="card" style={{ width: "18rem" }}>
                  <img src="..." className="card-img-top" alt="..." />
                  <div className="card-body">
                    <h5 className="card-title">{item.title}</h5>
                    <p className="card-text">{item.description}</p>
                    <button className="btn btn-primary">Go somewhere</button>
                  </div>
                </div>
              </div>
            )
          })
        }
      </div>
    </div>
  </div >
};
export default ProductsPage;
