import { useContext, useEffect, useState } from "react"
import { Link, useNavigate } from "react-router-dom"
import { StoreList } from "../Store/store-list"

function Header() {
  var [status, setStatus] = useState("undefined")
  const { setCartItemsList } = useContext(StoreList)
  const setCartItems = (allDet) => {
    if (sessionStorage.status) {
      const concernedUser = allDet.find((val) => val.Email === sessionStorage.email);
      setCartItemsList(concernedUser.cartItem);
    }
  };
  const getUsers = () => {
    fetch("http://localhost:8080/clients")
      .then((response) => response.json())
      .then((allDet) => setCartItems(allDet));
  };
  useEffect(() => {
    setStatus(sessionStorage.status);
    getUsers();
  }, [sessionStorage.status])
  const remove = () => {
    setStatus(undefined)
    sessionStorage.status = undefined;
  }
  const navigate = useNavigate();
  return <div className="container">
    <header className="d-flex flex-wrap align-items-center justify-content-center justify-content-md-between py-3 border-bottom">
      <div className="col-md-3 mb-2 mb-md-0">
        <a href="/" className="d-inline-flex link-body-emphasis text-decoration-none">
          Brand Logo
        </a>
      </div>

      <div className="col-md-3 text-end">
        <button type="button" className="btn btn-outline-primary me-2">Wishlist</button>
        {/* <button type="button" className="btn btn-primary">Add to cart</button> */}
        <Link className="btn btn-primary" to="/cart">Add to cart</Link>
        {(status === "undefined" || status === undefined) ? (
          <>
            <Link className="Logo" to="/signup">Sign Up</Link>
            <Link className="Logo" to="/login">LogIn</Link>
          </>
        ) : (
          <Link className="Logo" to="/" onClick={remove}>Sign Out</Link>
        )
        }
      </div >
    </header >
  </div >
};
export default Header;
