import { useLoaderData } from "react-router-dom";
import bed from "../Resources/bed.jpg"
function Products() {
  const categoriesList = useLoaderData();
  return (
    <div className="container products my-4" id="products">
      <h1 className="text-center">Products</h1>
      <div className="row row-cols-2 row-cols-sm-3 row-cols-lg-5">
        {categoriesList.map((item) => {
          <div className="col mb-4">
            <div className="card" style={{ width: "10rem", border: "none", cursor: "pointer" }}>
              <img src={bed} className="card-img-top" alt="..." style={{ borderRadius: "50%" }} />
              <div className="card-body">
                <h5 className="card-title text-center">{item}</h5>
              </div>
            </div>
          </div>
        })}
      </div>
    </div>
  )
};
export default Products;

export const categoriesLoader = () => {
  return fetch("http://localhost:8000/details")
    .then((res) => res.json())
    .then((data) => {
      return data[0].categories
    });
};
