import { useContext, useEffect, useState } from "react";
import { StoreList } from "../Store/store-list";
import axios from "axios";


function Cart() {
  const { cartItemsList, setCartItemsList } = useContext(StoreList);
  const [userDetails, setUserDetails] = useState([]);
  const [subtotal, setSubTotal] = useState(0);
  const setSubTotalFunc = () => {
    if (sessionStorage.email !== "" && sessionStorage.email !== undefined && sessionStorage.email !== "undefined") {
      console.log("May be it's me", sessionStorage.email)
      const concernedUser = userDetails.find((val) => val.Email === sessionStorage.email);
      console.log(concernedUser, "1");
      let total = 0;
      concernedUser.cartItem.map(item => total + (item.price * item.count))
      setSubTotal(total)
    }
  };

  const getUsers = () => {
    fetch("http://localhost:8080/clients")
      .then((response) => response.json())
      .then((allDet) => setUserDetails(allDet));
  };

  useEffect(() => {
    getUsers();
    setSubTotalFunc();
  }, []);
  const incrementFunc = (item) => {
    const concernedUser = userDetails.find((val) => val.Email === sessionStorage.email);
    const existingItemIndex = concernedUser.cartItem.findIndex(cartItem => cartItem.id === item.id);
    concernedUser.cartItem[existingItemIndex].count++;
    axios
      .put(`http://localhost:8080/clients/${concernedUser.id}`, concernedUser)
      .then((response) => setCartItemsList(response.data.cartItem))
  };
  const decrementFunc = (item) => {
    const concernedUser = userDetails.find((val) => val.Email === sessionStorage.email);
    const existingItemIndex = concernedUser.cartItem.findIndex(cartItem => cartItem.id === item.id);
    if (concernedUser.cartItem[existingItemIndex].count == 1) {
      concernedUser.cartItem.splice(existingItemIndex, 1);
    } else {
      concernedUser.cartItem[existingItemIndex].count--;
    }

    axios
      .put(`http://localhost:8080/clients/${concernedUser.id}`, concernedUser)
      .then((response) => setCartItemsList(response.data.cartItem))
  };
  return <div className="d-flex align-items-center py-4 bg-body-tertiary">
    <div className="container cart my-4">
      <h5 className="mb-4">Shopping Cart</h5>
      {cartItemsList.length > 0 ?
        <div className="cartContainer bg-light p-4">
          <div className="row row-cols-1 row-cols-sm-2">
            < div className="col addedProducts" >
              {cartItemsList.map((item) => {
                return (
                  <div className="d-flex bg-light my-4 selectedProduct" key={item.id}>
                    <img src="..." alt="..."></img>
                    <div>
                      <h6>{item.title}</h6>
                      <p>{item.description}</p>
                      <p><b>{item.price}</b></p>
                    </div>
                    <div className="d-flex flex-column">
                      <div className="d-flex align-items-center">
                        <button className="decrement" onClick={() => { decrementFunc(item) }}>-</button>
                        <p>{item.count}</p>
                        <button className="increment" onClick={() => incrementFunc(item)}> +</button>
                      </div>
                      <p>Rs.{item.price * item.count}</p>
                    </div>
                  </div>

                )
              })}
            </div>
            <div className="col details my-4">
              <h6>Cart Subtotal</h6>
              <div className="row">
                <div className="col">Subtotal</div>
                {cartItemsList.map((item) => {
                  return (
                    setSubTotal(item.price * item.count)
                  )
                })}
                <div className="col text-end">Rs{subtotal}</div>
              </div>
              <div className="row">
                <div className="col">Shipping</div>
                <div className="col text-end">Calculated at checkout</div>
              </div>
              <div className="row">
                <div className="col">Taxes</div>
                <div className="col text-end">Calculated at checkout</div>
              </div>
              <div className="row">
                <div className="col">Estimated Total</div>
                <div className="col text-end">Rs.XXX</div>
              </div>
              <button type="button" className="btn btn-primary w-100">Check Out</button>
            </div>
          </div>
        </div> :
        <div className="noCartContainer bg-light p-4">
          <div className="row-row-cols-1 row-cols-sm-2 d-flex align-items-center">
            <div className="col">
              <h6>Your shopping cart is empty</h6>
              <p>Fill it up and enjoy free shipping on orders above Rs.XXX</p>
            </div>
            <div className="col text-left text-sm-end">
              <button tye="button" className="btn btn-primary">Continue Shopping</button>
            </div>
          </div>
        </div>
      }
    </div>
  </div >
};
export default Cart;
