import { useRef } from "react";

function SignUp() {
  const userEmail = useRef();
  const userPassword = useRef();
  const addUser = (e) => {
    e.preventDefault()
    const newCredential = {
      Email: userEmail.current.value,
      Password: userPassword.current.value
    }

    userEmail.current.value = ""
    userPassword.current.value = ""
  };
  // begin---------------
  const save = (e) => {
    e.preventDefault();
    var result = clientlist.find(val => val.username === username || val.email === email)
    console.log(result)
    const goback = () => {
      setMessage(username + " registered successfully")
      setTimeout(() => {
        sessionStorage.status = true
        navigate("/")
      }, 3000)
    }

    if (name.length < 4) {
      setMessage("Length of name should be atleast 4");
    } else if (username.length < 3) {
      setMessage("Length of username should be atleast 3");
    } else if (!/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/.test(email)) {
      setMessage("Enter valid email address(abc@po.in)");
    } else if (!/^(?=.*?[A-Za-z])(?=.*?[0-9]).{8,}$/.test(password)) {
      setMessage("Enter a strong password");
      // 8 characters + alphanumeric 
    } else if (result) {
      setMessage("Username or email is already registered");
    } else {
      var clientInfo = {
        id: count,
        name: name,
        lastname: lastname,
        username: username,
        email: email,
        password: password,
        cartItem: []
      };
      const devEnv = process.env.NODE_ENV !== "production";
      const { REACT_APP_DEV_URL_C, REACT_APP_PROD_URL_C } = process.env;
      const url = `${devEnv ? REACT_APP_DEV_URL_C : REACT_APP_PROD_URL_C}`;
      axios
        .post(url, clientInfo)
        .then((response) => goback());
    }

  };
  // endshere------------------------
  return <div className="form-signup w-100 m-auto" style={{
    maxWidth: "500px",
    padding: "1rem"
  }}>
    <form onSubmit={(event) => addUser(event)}>
      <h1 className="h3 mb-3 fw-normal">Please sign in</h1>

      <div className="form-floating">
        <input type="email" className="form-control" id="floatingInput" placeholder="name@example.com" fdprocessedid="gwqkic" style={{
          marginBottom: "-1px",
          borderBottomRightRadius: "0px",
          borderBottomLeftRadius: "0px"
        }} ref={userEmail} />
        <label htmlFor="floatingInput">Email address</label>
      </div>
      <div className="form-floating">
        <input type="password" className="form-control" id="floatingPassword" placeholder="Password" fdprocessedid="7sau3s" style={{
          marginBottom: "10px",
          borderTopLeftRadius: "0px",
          borderTopRightRadius: "0px"
        }} ref={userPassword} />
        <label htmlFor="floatingPassword">Password</label>
      </div>

      <button className="btn btn-primary w-100 py-2" type="submit" fdprocessedid="d6z06">Sign in</button>
    </form>
  </div>
};
export default SignUp;
