import { Children, createContext, useEffect, useState } from "react";
import { useNavigate } from 'react-router-dom';

export const StoreList = createContext({
  SetSelectedCategory: () => { }
})

export const StoreListProvider = ({ children }) => {
  const [selectedCategory, setSelectedCategory] = useState("")
  const navigate = useNavigate();

  const SetSelectedCategory = (item) => {
    setSelectedCategory(item)
    navigate('/products');
  }

  return (
    < StoreList.Provider value={{ SetSelectedCategory }}>
      {children}
    </StoreList.Provider >
  )
}

export const categoriesLoader = () => {
  return fetch("http://localhost:8000/details")
    .then((res) => res.json())
    .then((data) => {
      return data[0]
    });
};
